import { useNavigate } from "react-router-dom"
import { useState, useRef, useContext, useEffect } from "react"

import MyHeader from "./MyHeader"
import MyButton from "./MyButton"
import EmotionItem from "./EmotionItem"
import { DiaryDispatchContext } from "../App"

const env = process.env
env.PUBLIC_URL = env.PUBLIC_URL || ""

// 감정 배열
const emotionList = [
  {
    emotion_id: 5,
    emotion_img: process.env.PUBLIC_URL + `/assets/5.png`,
    emotion_descript: '매우 좋음'
  },
  {
    emotion_id: 4,
    emotion_img: process.env.PUBLIC_URL + `/assets/4.png`,
    emotion_descript: '좋음'
  },
  {
    emotion_id: 3,
    emotion_img: process.env.PUBLIC_URL + `/assets/3.png`,
    emotion_descript: '보통'
  },
  {
    emotion_id: 2,
    emotion_img: process.env.PUBLIC_URL + `/assets/2.png`,
    emotion_descript: '나쁨'
  },
  {
    emotion_id: 1,
    emotion_img: process.env.PUBLIC_URL + `/assets/1.png`,
    emotion_descript: '최악'
  }
]


// input 요소 날짜 기본값
const getStringDate = (date) => {
  return date.toISOString().slice(0, 10)
  // ISO 형식의 문자열을 반환, YYYY-MM-DDTHH...
}


const DiaryEditor = ({ isEdit, orginData }) => {
  const [emotion, setEmotion] = useState(3)
  // 선택한 감정값 저장하는 State
  const [date, setDate] = useState(getStringDate(new Date()))
  // input 날짜 값을 저장하는 State
  const contentRef = useRef()
  const [content, setContent] = useState("")
  // 일기 내용 저장하는 state

  const handleClickEmote = (emotion) => {
    setEmotion(emotion)
  }

  const navigate = useNavigate()

  const { onCreate } = useContext(DiaryDispatchContext)
  const handelSubmit = () => {
    if (content.length < 1) {
      contentRef.current.focus()
      return
    }
    onCreate(date, content, emotion)
    navigate('/', { replace: true })
  }

  useEffect(() => {
    if (isEdit) {// 현재 isEdit을 true일 때만 로직 동작
      setDate(getStringDate(new Date(parseInt(orginData.date))))
      setEmotion(orginData.emotion)
      setContent(orginData.content)
    }
  }, [isEdit, orginData])

  return <div className="DiaryEditor">
    <MyHeader headText={"일기 쓰기"} leftChild={<MyButton text={"<"} onClick={() => navigate(-1)} />} />

    <div>
      <section>
        <h4>오늘 날짜는?</h4>
        <div className="input_box">
          <input className="input_date" value={date} onChange={(e) => setDate(e.target.value)} type="date" />
        </div>
      </section>
      <section>
        <h4>현재 기분은?</h4>
        <div className="input_box emotion_list_wrapper">
          {emotionList.map((it) => (
            <EmotionItem
              key={it.emotion_id} {...it}
              onClick={handleClickEmote}
              isSelected={it.emotion_id === emotion}
            />
          ))}
        </div>
      </section>
      <section>
        <h4>내용</h4>
        <div className="input_box text_wrapper">
          <textarea
            placeholder="오늘 하루 어땠나요?"
            ref={contentRef}
            value={content}
            onChange={(e) => setContent(e.target.value)} />
        </div>
      </section>
      <section>
        <div className="control_box">
          <MyButton text={'취소'} onClick={() => navigate(-1)} />
          <MyButton text={'완료'} type={"positive"} onClick={handelSubmit} />
        </div>
      </section>
    </div>
  </div>
}

export default DiaryEditor
